import React, { Fragment } from 'react'
import Grid from '@material-ui/core/Grid'
import Card from '../Card'
import TextField from '@material-ui/core/TextField'
import HeroStats from "./HeroStats"
import { Typography } from '@material-ui/core';
import { withStyles } from '@material-ui/core/styles';

import Paper from '@material-ui/core/Paper';


const styles = theme => ({
    root: {
      flexGrow: 1,
      ...theme.mixins.gutters(),
      paddingTop: theme.spacing.unit * 2,
      paddingBottom: theme.spacing.unit * 2,
    },
    paper: {
      height: 140,
      width: 100,
    },
    control: {
      padding: theme.spacing.unit * 2,
    },
  });


class PlayerView extends React.Component {
    
    state = {
        players: [],
        player: "Muma",
        hero: "Tracer",
    }

    componentDidMount() {
        this.getPlayers()
        this.state.players.map((item, index) => {return <div key={index}>{JSON.stringify(item)}</div>})
        alert(this.state.players.findIndex(players => players.Name === 'Muma'))
      }
    
    getPlayers = _ => {
        fetch('http://localhost:4000/players')
        .then(response => response.json())
        .then(response => this.setState({ players: response.data}))
        .catch(err => console.error(err))
      }
    
    render() {
      return (
        <Fragment>
            <div>
                   {
                    this.state.players.map((item, index) => {return <div key={index}>{JSON.stringify(item)}</div>}
                    )}
                </div>
            <Grid container justify="center" alignItems="center" spacing={16}> 
                <Grid item> 
                    <TextField
                        id="standard-name"
                        label="Player"
                        margin="normal"
                        defaultValue={this.state.player}
                    />
                    <Card 
                        key={0} 
                        name="Muma" 
                        team="Houston Outlaws" 
                        role="Tank"
                        picture="https://bnetcmsus-a.akamaihd.net/cms/page_media/EZTH4390SJCR1526600596230.png"
                    />
                </Grid>
                <Grid item justify="center">                
                    <HeroStats />
                </Grid>
         </Grid>
        </Fragment>
      )
    }
}

export default withStyles(styles)(PlayerView);
